mapper.select:
select count(*)
  from acountBook
 where mem_no = #{memNo}

mapper.insert:
insert
  into acountbook
    (
      mem_no
    , payment_date
    , deal_divide
    , deal_type
    , compendiums
    , amount
    )
    values
    (
      #{memNo}
    , #{paymentDate}
    , #{dealDivide}
    , #{dealType}
    , #{compendiums}
    , #{amount}
    )
