mapper.insert:
INSERT INTO 
    MEMBER ( 
	        MEM_NO
	      , MEM_ID
	      , MEM_PWD
	      , MEM_NAME
	      , EMAIL
	      , PHONE
      ) VALUES 
      ( 
	          SEQ_MEMBER.NEXTVAL
	       , #{memId}
	       , #{memPwd}
	       , #{memName}
	       , #{email}
	       , #{phone}
       )

mapper.select:
SELECT COUNT(*)
  FROM MEMBER
  WHERE MEM_ID = #{memId}

