mapper.select:
SELECT MEM_ID, ENROLL_DATE
  FROM MEMBER
  WHERE EMAIL=#{email}

SELECT MEM_PWD
    FROM MEMBER
    WHERE MEM_ID=#{memId}

mapper.update:
update member
   set mem_id = #{memId}
   	 , mem_pwd = #{memPwd}
   	 , mem_name = #{memName}
     , email = #{email}
     , phone = #{phone}
     , enroll_date = sysdate
 where mem_id = #{memId}

update member
   set status = 'N'
 where mem_id = #{memId}

update member
   set mem_id = #{memId}
   	 , mem_pwd = #{memPwd}
   	 , mem_name = #{memName}
     , email = #{email}
     , phone = #{phone}
     , enroll_date = sysdate
 where mem_no = #{memNo}

UPDATE MEMBER
  SET MEM_PWD = #{memPwd}
  WHERE EMAIL = #{email} 
    AND MEM_ID = #{memId}
