mapper.select:
select count(*) as
"count"
from stock_choose
where mem_no = #{memNo}
and stock_no =
#{stockNo}

mapper.insert:
insert
into stock_choose
(
choose_no
, mem_no
, stock_no
)
values
(
stock_choose_seq.nextval
, #{memNo}
, #{stockNo}
)

mapper.delete:
delete from stock_choose
where mem_no = #{memNo}
and
stock_no = #{stockNo}

mapper.insert:
insert
into stock_buy
(
buy_no
, mem_no
, stock_account
)
values
(
stock_buy_seq.nextval
, #{memNo}
, #{stockAccount}
)
